import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import useFetch from "./useFetch";
import "../styles/Movie.css"

const Movie = () => {
    let { id } = useParams();
    const { error: movieError, isPending: moviePending, data: movie } = useFetch(`http://localhost:8000/movie/${id}`);
    const { error: reviewError, isPending: reviewPending, data: reviews } = useFetch(`http://localhost:8000/reviews`);

    const movieReviews = reviews.filter((review) => review.id === id);

    return (
        <div className="movieDetails">
        { movieError && <div className="movieDetails">{ movieError }</div> }
        { moviePending && <div className="movieDetails">Loading...</div> }
        { movie &&  
            <div className="movies">
                <div className="movieDetails">
                    <div>
                    <img src={process.env.PUBLIC_URL + movie.img} />
                    </div>
                    <div className="info">
                    <p>Description: { movie.Description }</p>
                    <p>Release: { movie.release }</p>
                    <p>Duration: { movie.duration }</p>
                    </div>
                </div> 
                <div>
                    {movieReviews.map(review => (
                        <div className="reviews" key={review.id} >
                            <div className="info">
                                <p>Username: { review.username }</p>
                                <p>Review: { review.text }</p>
                                <p>Star: { review.star }</p>
                            </div>
                        </div>
                    ))}
                </div> 
            </div>
        }
        </div>

    );
}

export default Movie;
