import React, { useState, useEffect } from "react";
import { Link, useHistory } from "react-router-dom";
import "../styles/Navbar.css";
import useFetch from "./useFetch";

const Navbar = ({ username, onLogout }) => {
  const firstLetter = username ? username.charAt(0).toUpperCase() : "";
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [searchSuggestions, setSearchSuggestions] = useState([]);
  const history = useHistory();

  const { error, isPending, data: movies } = useFetch('http://localhost:8000/movies');

  useEffect(() => {
    if (movies === null) {
      return; // Do nothing if movies data is not yet fetched
    }

    if (searchQuery === "") {
      setSearchSuggestions([]);
      return;
    }

    const suggestions = movies
      .filter(movie => movie.name.toLowerCase().includes(searchQuery.toLowerCase()))
      .map(movie => movie.name);

    setSearchSuggestions(suggestions);
  }, [searchQuery, movies]);

  const handleToggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const handleLogout = () => {
    onLogout();
    history.push('/login');
  };

  const handleSearch = (e) => {
    setSearchQuery(e.target.value);
  };

  return (
    <nav className="navbar">
      <div className="hamburger" onClick={handleToggleMenu}>
        <a><img src="/hamburger.png" alt="Hamburger" /></a>
      </div>
      <div className={`links ${isMenuOpen ? "show" : ""}`}>
        <Link to="/home" onClick={handleToggleMenu}>Home</Link>
        <Link to="/browse" onClick={handleToggleMenu}>All Movies</Link>
        <Link to="/about" onClick={handleToggleMenu}>About</Link>
      </div>

      <div className="userinfo">
        <div className="search-bar">
          <input type="text" placeholder="Search Movies" value={searchQuery} onChange={handleSearch} list="searchSuggestions" />
          <datalist id="searchSuggestions">
            {searchSuggestions.map((suggestion, index) => (
              <option key={index} value={suggestion} />
            ))}
          </datalist>
          <button onClick={() => history.push(`/search/${searchQuery}`)}>Search</button>
        </div>
        <Link to={`/update-profile/${username}`}>{username}</Link>
        <div className="profile-circle">{firstLetter}</div>
        <button className="logout-btn" onClick={handleLogout}> Logout </button>
      </div>
    </nav>
  );
};

export default Navbar;
