import React, { useState } from 'react';
import "../styles/Login.css";
import { Link, useHistory } from 'react-router-dom';

const Login = ({ onLogin }) => { // Step 1: Receive the onLogin prop
  // React States
  const [errorMessages, setErrorMessages] = useState({});
  const [isSubmitted, setIsSubmitted] = useState(false);
  const history = useHistory();
  // User Login info
  const database = [
    {
      username: "me",
      password: "p"
    },
    {
      username: "mehak",
      password: "mehak"
    }
  ];

  const errors = {
    uname: "invalid username",
    pass: "invalid password"
  };

  const handleSubmit = (event) => {
    // Prevent page reload
    event.preventDefault();

    const { uname, pass } = event.target.elements; // Step 2: Correctly access form elements

    // Find user login info
    const userData = database.find((user) => user.username === uname.value);

    // Compare user info
    if (userData) {
      if (userData.password !== pass.value) {
        // Invalid password
        setErrorMessages({ name: "pass", message: errors.pass });
      } else {
        setIsSubmitted(true);
        // Call the onLogin function with the username upon successful login
        onLogin(userData.username); // Step 3: Pass the username to the parent component
        history.push('/home');
      }
    } else {
      // Username not found
      setErrorMessages({ name: "uname", message: errors.uname });
    }
  };

  // Generate JSX code for error message
  const renderErrorMessage = (name) =>
    name === errorMessages.name && (
      <div className="error">{errorMessages.message}</div>
    );

  // JSX code for login form
  const renderForm = (
    <div className="form">
      <form onSubmit={handleSubmit}>
        <div className="input-container">
          <label>Username </label>
          <input type="text" name="uname" required />
          {renderErrorMessage("uname")}
        </div>
        <div className="input-container">
          <label>Password </label>
          <input type="password" name="pass" required />
          {renderErrorMessage("pass")}
          <button className="submit" type="submit">Sign In</button>
        </div>
        <br />
        <div className="have-account">
          <span>Don't have an account? </span>
          <button className="signup" onClick={() => history.push('/create-account')}>Sign Up</button>
        </div>
      </form>
    </div>
  );

  return (
    <div className="app">
      <div className="login-form">
        <div className="title">Login</div>
        {isSubmitted ? <div>User is successfully logged in</div> : renderForm}
      </div>
    </div>
  );
};

export default Login;
