import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Login from './components/Login.js';
import Home from './components/Home.js';
import Navbar from './components/Navbar.js';

const UsernameContext = React.createContext('');

class App extends Component {
  constructor(props) {
    super(props);

    // Check if the username exists in localStorage
    const storedUsername = localStorage.getItem('username');

    // Set the initial state with the username (if available)
    this.state = {
      username: storedUsername || '', // Set to an empty string if username doesn't exist
    };
  }

  // Function to update the username in the state and localStorage
  updateUsername = (newUsername) => {
    this.setState({ username: newUsername });
    localStorage.setItem('username', newUsername);
  };

  render() {
    const { username } = this.state;

    return (
      <UsernameContext.Provider value={username}>
        <Router>
          <div className="App">
            <div className="content">
              <Switch>
                {/* Use the `render` prop with a function */}
                <Route
                  exact
                  path="/login"
                  render={(props) => <Login {...props} onLogin={this.updateUsername} />}
                />
                <div>
                  {/* Pass the `username` as a prop to Navbar */}
                  <Navbar username={username} />
                  {/* Use the `render` prop with a function */}
                  <Route
                    exact
                    path="/home"
                    render={(props) => <Home {...props} username={username} />}
                  />
                </div>
              </Switch>
            </div>
          </div>
        </Router>
      </UsernameContext.Provider>
    );
  }
}

export default App;
