import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import useFetch from "./useFetch";

const Search = () => {
  const { query } = useParams();
  const [movies, setMovies] = useState([]);
  const { error, isPending, data: moviesData } = useFetch(`http://localhost:8000/movie?search=${query}`);

  useEffect(() => {
    if (moviesData) {
      // Filter and sort movies based on relevance to the query
      const filteredMovies = moviesData.filter(movie => {
        const lowerCaseQuery = query.toLowerCase();
        return (
          movie.name && movie.name.toLowerCase().includes(lowerCaseQuery) ||
          movie.description && movie.description.toLowerCase().includes(lowerCaseQuery)
        );
      });

      // Sort filtered movies based on relevance (you can customize this sorting logic)
      const sortedMovies = filteredMovies.sort((a, b) => {
        const relevanceA = calculateRelevance(a, query);
        const relevanceB = calculateRelevance(b, query);
        return relevanceB - relevanceA; // Sort in descending order of relevance
      });

      setMovies(sortedMovies);
    }
  }, [moviesData, query]);

  // Calculate the relevance score for a movie based on the query (you can customize this calculation)
  const calculateRelevance = (movie, query) => {
    const lowerCaseQuery = query.toLowerCase();
    const nameScore = movie.name && movie.name.toLowerCase().includes(lowerCaseQuery) ? 1 : 0;
    const descriptionScore = movie.description && movie.description.toLowerCase().includes(lowerCaseQuery) ? 0.5 : 0;
    return nameScore + descriptionScore;
  };

  return (
    <div>
      <h2>Search Results for "{query}"</h2>
      {isPending && <div>Loading...</div>}
      {error && <div>{error}</div>}
      {movies.map(movie => (
        <div key={movie.id}>
          <Link to={`/movie/${movie.id}`}>
          <img src={process.env.PUBLIC_URL + movie.img} alt={movie.title} />
          </Link>
          <p>{movie.description}</p>
        </div>
      ))}
    </div>
  );
};

export default Search;
