import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import useFetch from "./useFetch";

const Search = () => {
  const { query } = useParams();
  const [movies, setMovies] = useState([]);
  const { error, isPending, data: moviesData } = useFetch(`http://localhost:8000/movie?search=${query}`);

  useEffect(() => {
    if (moviesData) {
      // Filter and sort movies based on relevance to the query
      const filteredMovies = moviesData.filter(movie => {
        const lowerCaseQuery = query.toLowerCase();
        return (
          movie.name && movie.name.toLowerCase().includes(lowerCaseQuery) ||
          movie.description && movie.description.toLowerCase().includes(lowerCaseQuery)
        );
      });

      setMovies(filteredMovies);
    }
  }, [moviesData, query]);

  // Calculate the relevance score for a movie based on the query (you can customize this calculation)
  const calculateRelevance = (movie, query) => {
    const lowerCaseQuery = query.toLowerCase();
    const nameScore = movie.name && movie.name.toLowerCase().includes(lowerCaseQuery) ? 1 : 0;
    const descriptionScore = movie.description && movie.description.toLowerCase().includes(lowerCaseQuery) ? 0.5 : 0;
    return nameScore + descriptionScore;
  };

  return (
    <div className="home">
      <div style={{ display: "grid", alignContent: "center" }}>
        <h2 style={{color: "#efef83"}}>Search Results for "{query}"</h2>
        {isPending && <div>Loading...</div>}
        {error && <div>{error}</div>}
        {movies.length === 0 && !isPending && <p style={{color: "#efef83"}}>No Movies Found</p>}
        {movies.map(movie => (
          <div key={movie.id}>
            <div className="movie-preview" key={movie.id} >
              <div className="image">
                <Link to={`/movie/${movie.id}`}>
                  <img src={process.env.PUBLIC_URL + movie.img} alt={movie.name} />
                </Link>
              </div>
              <div className="info">
                <Link to={`/movie/${movie.id}`}>
                  <h2>{ movie.name }</h2>
                </Link>
                <p>Description: { movie.description }</p>
                <p>Release: { movie.release }</p>
                <p>Duration: { movie.duration }</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );

};

export default Search;
