import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const Movie = () => {
    const { id } = useParams();
    const [movie, setMovie] = useState(null);

    useEffect(() => {
      // Fetch the movie description using the id from the database
      const fetchMovieDescription = async () => {
        try {
          const response = await axios.get(`API_ENDPOINT/movies/${id}`);
          setMovie(response.data);
        } catch (error) {
          console.error("Error fetching movie description:", error);
        }
      };

      fetchMovieDescription();
    }, [id]);

    return (
      <div>
        {movie ? (
          <div>
            <h2>{movie.title}</h2>
            <p>{movie.description}</p>
            {/* Display other movie details here */}
          </div>
        ) : (
          <div>Loading...</div>
        )}
      </div>
    );
}

export default Movie;
